import Signup from "./Signup";
import Navbar from "./Navbar";
import React, { useState,useEffect} from "react";
import theme from "./theme";
import Home from "../Home";
import { Grid } from "@material-ui/core";
import Login from "./Login";
import Products from "./products/products";
import Navbar1 from "./Navbar/Navbarlocal";
import {commerce} from '../lib/commerce'
import Cart from './Cart/Cart'
export default function App() {
  const[log,setlog]=useState(false);
  const[products,setProducts]=useState([]);
  const[cart,setcart]=useState({})
  console.log(cart.line_items.length)
  //handles logs
  function hangleLog(){
    setlog(prev=>!prev)
  }
const product=[]
  //fetch data
  const fetchdata= async ()=>{
    const {data} = await commerce.products.list();
    setProducts(data)
  }  

  //fetch cart
  const fetchCart=async ()=>{
    setcart(await commerce.cart.retrieve())
  }

//useEffect for fetching data
useEffect(()=>{fetchdata()
  fetchCart()},[])

  //handlecart

  const handleCart=async (productid,quantity)=>{
    const item =await commerce.cart.add(productid,quantity)
    setcart(item.line_items)
  }
  return (
    <>
    <Navbar1 totalitems={cart.total_items}/>
    {/* <Products products={products} addToCart={handleCart} /> */}
    <Cart cart={cart}/>
        {/* <Navbar changelog={hangleLog} />
