    padding: theme.spacing(3),
    color: "#1B224A",
  },
  text1: {
    fontFamily: "Rosarivo",
    fontSize: "20px",
    padding: theme.spacing(1),
    color: "#1B224A",
    textAlign:'center'
  },
  root: {
    maxWidth: 445,
    textAlign: "center",
  },
}));
