import React from "react";
import { Typography, Button, Divider } from "@material-ui/core";
import {
  Elements,
  CardElement,
  ElementConsumer,
} from "@stripe/react-stripe-js";
import { loadStripe } from "@stripe/stripe-js";
import Review from "./Review";
const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLIC_KEY);

function PaymentForm(props) {
  const handleSubmit = async (event, elements, stripe) => {
    event.preventDefault();
    if (!stripe || !elements) {
      return;
    }
    const cartElement = elements.getElement(CardElement);
    const { error, paymentMethod } = await stripe.createPaymentMethod({
      type: "card",
      card: cartElement,
    });

    if (error) {
      console.log("[error]", error);
    } else {
      const orderData = {
        line_items: props.checkOutToken.live.line_items,
        customer: {
          firstname: props.shippingData.firstName,
          lastname: props.shippingData.lastName,
          email: props.shippingData.email,
        },
        shipping: {
          name: "International",
          street: props.shippingData.address1,
          town_city: props.shippingData.city,
          county_state: props.shippingData.shippingSubdivision,
          postal_zip_code: props.shippingData.zip,
          country: props.shippingData.shippingCountry,
        },
        fulfillment: { shipping_method: props.shippingData.shippingOption },
        payment: {
          gateway: "stripe",
          stripe: {
            payment_method_id: paymentMethod.id,
          },
        },
      };
    }
    props.handleCaptureCheckout( orderData)
  };
  return (
    <>
      <Review token={props.checkOutToken} />
      <Divider />
      <Typography variant="h6" gutterBottom style={{ margin: "20px 0" }}>
        Payment Method
      </Typography>
      <Elements stripe={stripePromise}>
        <ElementConsumer>
          {({ elements, stripe }) => (
            <form onSubmit={(e) => handleSubmit(e, elements, stripe)}>
              <CardElement>
                <br />
                <br />
                <div
                  style={{ display: "flex", justifyContent: "space-between" }}
                >
                  <Button
                    variant="outlined"
                    onClick={props.backstep}
                    color="secondary"
                  >
                    Back
                  </Button>
                  <Button
                    type="submit"
                    variant="contained"
                    disabled={!stripe}
                    color="primary"
                  >
                    Pay{" "}
                    {props.checkOutToken.live.subtotal.formatted_with_symbol}
                  </Button>
                </div>
              </CardElement>
            </form>
          )}
        </ElementConsumer>
      </Elements>
    </>
  );
}

export default PaymentForm;
