import React,{useRef,useState} from "react";
import { Button, TextField } from "@material-ui/core";
import {
  Card,
  makeStyles,
  CardContent,
  Typography,
} from "@material-ui/core";
import theme from "./theme";
import {useAuth} from '../contexts/AutnContext'
const useStyles = makeStyles((theme) => ({
  button: { ...theme.mybutton },
  nav: { ...theme.myNav },
  small: {
    display: "none",
    [theme.breakpoints.up("sm")]: {
      display: "block",
    },
  },
  large: {
    display: "block",
    [theme.breakpoints.up("sm")]: {
      display: "none",
    },
  },
  textarea: {
    ...theme.textarea,
  },
  text: {
    fontFamily: "Rosarivo",
    fontSize: "50px",
    padding: theme.spacing(3),
    color: "#1B224A",
  },
  text1: {
    fontFamily: "Rosarivo",
    fontSize: "20px",
    padding: theme.spacing(1),
    color: "#1B224A",
    textAlign:'center'
  },
  root: {
    maxWidth: 445,
    textAlign: "center",
  },
}));
export default function Signup() {
  const classes = useStyles();
  const emailRef=useRef();
  const passwordRef=useRef();
  const passwordConfirmRef=useRef();
  const {signup,currentUser}=useAuth();
const[error,setError]=useState('');
const[loading,setLoading]=useState(false);
const [email,setRegisterEmail]=useState('');
const [password,setRegisterPassword]=useState('');

async function handleSubmit(e) {
  e.preventDefault()

  if (passwordRef.current.value !== passwordConfirmRef.current.value) {
    return setError("Passwords do not match")
  }

  try {
    setError("")
    setLoading(true)
    await signup(emailRef.current.value, passwordRef.current.value)
  } catch {
    setError("Failed to create an account")
  }

  setLoading(false)
}
  return (
    <div style={{ backgroundColor: "#C7DFC1", height: "90vh" }}>
      <Typography variant="h1" className={classes.text}>
        Buy and Ship to Anywhere in the World
      </Typography>
      <Card
        className={classes.root}
        style={{ marginLeft: "120px", marginTop: "10px" }}
      >
        <CardContent>
          <form onSubmit={handleSubmit} className={classes.root}>
          <Typography>{error}</Typography>
            <Typography
              gutterBottom
              variant="h5"
              component="h2"
              style={{ textAlign: "center", fontFamily: "Rosarivo" }}
            >
              Sign Up
            </Typography>
            <input type='text' />
            <input type='text'/>
            <input type='text'/>
            <TextField
              required
              ref={emailRef}
              className={classes.textarea}
              id="email"
              label="Email Address"
              variant="outlined"
            />
            <br />
            <TextField
              type="password"
              required
              ref={passwordRef}
              className={classes.textarea}
              id="password"
              label="Password"
              variant="outlined"
            />
            <br />
            <TextField
              required
              type="password"
              ref={passwordConfirmRef}
              className={classes.textarea}
              id="password-confirm"
              label="Confirm Password"
              variant="outlined"
            />
            <br />
            <button
            disabled={loading}
              variant="contained"
              type="submit"
              color="primary"
              className={classes.button}
              style={{ marginLeft: theme.spacing(6), width: "150px" }}
            >
              Sign Up
            </button>
          </form>
          
        </CardContent>
      </Card>
      <Typography className={classes.text1}>
              Already have an account? Log In
          </Typography>
    </div>
  );
}
