import React,{useState} from 'react'
import {
    Card,
    makeStyles,
    CardContent,
    Typography,
  } from "@material-ui/core";

  const useStyles = makeStyles((theme) => ({
    button: { ...theme.mybutton },
    nav: { ...theme.myNav },
    small: {
      display: "none",
      [theme.breakpoints.up("sm")]: {
        display: "block",
      },
    },
    large: {
      display: "block",
      [theme.breakpoints.up("sm")]: {
        display: "none",
      },
    },
    textarea: {
      ...theme.textarea,
    },
    text: {
      fontFamily: "Rosarivo",
      fontSize: "50px",
      padding: theme.spacing(3),
      color: "#1B224A",
    },
    text1: {
      fontFamily: "Rosarivo",
      fontSize: "20px",
      padding: theme.spacing(1),
      color: "#1B224A",
      textAlign:'center'
    },
    root: {
      maxWidth: 445,
      textAlign: "center",
    },
  }));
function Login() {
    const classes = useStyles();
    const[email,setLoginEmail]=useState('');
    const[password,setLoginPassword]=useState('');


    return (
        <div style={{ backgroundColor: "#C7DFC1", height: "90vh" }}>
        <Typography variant="h1" className={classes.text}>
          Buy and Ship to Anywhere in the World
        </Typography>
        <Card
          className={classes.root}
          style={{ marginLeft: "120px", marginTop: "10px" }}
        >
          <CardContent>
            <form className={classes.root}>
              <Typography
                gutterBottom
                variant="h5"
                component="h2"
                style={{ textAlign: "center", fontFamily: "Rosarivo" }}
              >
                Sign Up
              </Typography>
              <input required placeholder="Email Address" className={classes.textarea} type='text' onChange={(event)=>{setLoginEmail(event.target.value)}}/><br/>
              <input required placeholder="Password"  className={classes.textarea} type='password'  onChange={(event)=>{setLoginPassword(event.target.value)}}/>
              <br />
              <button
                type="submit"
                className={classes.button}
                style={{width: "150px",padding:'1vh'}}
              >Log In</button>
            </form>
            
          </CardContent>
        </Card>
        <Typography className={classes.text1}>
              Do not have an account? Sign Up.
          </Typography>
      </div>
    )
}

export default Login
