import { createContext, useState, useEffect, useContext} from "react";
import { createUserWithEmailAndPassword, onAuthStateChanged, sendPasswordResetEmail, signInWithEmailAndPassword, signOut, updateProfile } from "firebase/auth";
import { auth } from ".././firebase";
const userContext = createContext({});

export const useUserContext=()=>useContext(userContext)
export const Context = ({ childern }) => {
  const [user, setUser] = useState(null);
  const [loading, setloading] = useState();
  const [error, seterror] = useState("");

  useEffect(()=>{
      setloading(true)
     const unsubscribe= onAuthStateChanged(auth,(res)=>res?setUser(res):setUser(null))
      seterror('')
      setloading(false)
      return unsubscribe
  },[]);

  const registerUser = (email, name, password) => {
    setloading(true);
    createUserWithEmailAndPassword(auth, email, password).then(() => {
      return updateProfile(auth.currentUser, {
        displayName: name,
      })
        .then((res) => console.log(res))
        .catch((err) => seterror(err.message))
        .finally(() => setloading(false));
    });
  };
  const signInUser = (email, password) => {
      setloading(true);
      signInWithEmailAndPassword(auth,email,password)
      .then(res=>console.log(res))
      .catch(err=>seterror(err.message))
      .finally(()=>setloading(false))
  };
  const logOutUser = () => {signOut(auth)};
  const forgotPassword = (email) => {sendPasswordResetEmail(auth,email)};
  const contextvalue = {
      error,loading,user,signInUser,registerUser,forgotPassword,logOutUser
  };
  return <userContext.Provider value={contextvalue}></userContext.Provider>;
};
